def get_non_stopwords(text):
    stopwords = set(stopwords_no_relations)
    words = [str(s).lower() for s in word_tokenize(text)]
    
    #take out the stop words
    words_ret = set([])
    for w in words:
        if(not w in stopwords):
            words_ret.add(w)
            
    return words_ret
